package inheritenceDemo;

public class inheritenceTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		car c=new car();
		maruti m=new maruti();
		toyota t=new toyota();
		System.out.println(c.capacity());
		System.out.println(c.year());
		System.out.println(c.horn());
		System.out.println(m.capacity());
		System.out.println(m.year());
		System.out.println(m.horn());
		System.out.println(m.powerSteering());
		System.out.println(t.capacity());
		System.out.println(t.year());
		System.out.println(t.at());
		System.out.println(t.getCapacity());
		System.out.println(t.horn());
		System.out.println(t.sunroof());
		

	}

}
